import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def point_to_line_distance(p, line_start, line_end):
    return abs((line_end[0] - line_start[0]) * (line_start[1] - p[1]) - (line_start[0] - p[0]) * (line_end[1] - line_start[1])) / math.sqrt((line_end[0] - line_start[0])**2 + (line_end[1] - line_start[1])**2)

def find_most_distant_point(vertices):
    max_distance = 0
    for i in range(len(vertices)):
        distance_to_line = point_to_line_distance(vertices[i], vertices[(i-1)%len(vertices)], vertices[(i+1)%len(vertices)])
        if distance_to_line > max_distance:
            max_distance = distance_to_line
    return max_distance

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        vertices = []
        for _ in range(n):
            x, y = map(int, input().split())
            vertices.append((x, y))
        print(f"{find_most_distant_point(vertices):.6f}")

if __name__ == "__main__":
    main()
