#include <stdio.h>
#include <math.h>

#define MAX 20

int is_prime(int n) {
    if (n <= 1) return 0;
    if (n <= 3) return 1;
    if (n % 2 == 0 || n % 3 == 0) return 0;
    for (int i = 5; i * i <= n; i += 6)
        if (n % i == 0 || n % (i + 2) == 0) return 0;
    return 1;
}

int gcd(int a, int b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

void print_coefficients(int a, int m, int b, int n) {
    int d = m * n;
    int coefficients[d + 1];
    coefficients[d] = 1;

    for (int i = 0; i < d; i++) {
        coefficients[i] = 0;
    }

    for (int i = 0; i < d; i++) {
        int x = pow(a, (d - i) / m);
        int y = pow(b, (d - i) / n);
        int z = (d - i) % m;
        int w = (d - i) % n;
        coefficients[i] += x * pow(a, z);
        coefficients[i] += y * pow(b, w);
    }

    for (int i = d; i >= 0; i--) {
        if (coefficients[i] != 0) {
            printf("%d", coefficients[i]);
            if (i > 0) printf(" ");
            else printf("\n");
        }
    }
}

