#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

int gcd(int a, int b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

vector<int> minimal_polynomial(int a, int m, int b, int n) {
    int d = m * n;
    vector<int> poly(d + 1);
    poly[d] = 1;

    for (int i = 0; i < d; i++) {
        int sign = (i % 2 == 0) ? 1 : -1;
        int term = 0;
        for (int j = 0; j <= i; j++) {
            term += sign * (m * m) * (n * n) * pow(a, i - j) * pow(b, j);
            sign *= -1;
        }
        poly[d - i - 1] = term;
    }

    int g = gcd(poly[0], poly[d]);
    for (int i = 0; i <= d; i++) {
        poly[i] /= g;
    }

    return poly;
}

int main() {
    int a, m, b, n;
    while (cin >> a >> m >> b >> n) {
        if (a == 0 && b == 0 && m == 0 && n == 0)
            break;

        vector<int> poly = minimal_polynomial(a, m, b, n);
        for (int i = poly.size() - 1; i >= 0; i--) {
            if (poly[i] > 0)
                cout << poly[i];
            else
                cout << -poly[i];
            if (i > 0)
                cout << " ";
        }
        cout << endl;
    }

    return 0;
}
