import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int a = scanner.nextInt();
            int m = scanner.nextInt();
            int b = scanner.nextInt();
            int n = scanner.nextInt();
            if (a == 0 && b == 0 && m == 0 && n == 0) {
                break;
            }
            int[] coefficients = getMinimalPolynomial(a, m, b, n);
            for (int i = coefficients.length - 1; i >= 0; i--) {
                System.out.print(coefficients[i]);
                if (i > 0) {
                    System.out.print(" ");
                }
            }
            System.out.println();
        }
    }

    public static int[] getMinimalPolynomial(int a, int m, int b, int n) {
        int[] coefficients = new int[(m * n) + 1];
        coefficients[0] = 1;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                coefficients[i * n + j] = 1;
            }
        }
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                coefficients[(m * n) - (i * n + j)] = -a * (m - i) * (n - j);
            }
        }
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                coefficients[(m * n) - (i * n + j)] += a * (m - i) * (n - j) * coefficients[(m * n) - 1 - (i * n + j)];
            }
        }
        for (int i = 0; i < m * n; i++) {
            int gcd = gcd(coefficients[i], coefficients[(m * n) - 1]);
            coefficients[i] /= gcd;
            coefficients[(m * n) - 1] /= gcd;
        }
        return coefficients;
    }

    public static int gcd(int a, int b) {
        if (b == 0) {
            return a;
        }
        return gcd(b, a % b);
    }
}
