import math

def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def minimal_polynomial(a, m, b, n):
    if m == 1 or n == 1:
        return [1, -a, 1]
    if m == n:
        return [1] + [0] * (m * n - 2) + [1]
    if m > n:
        m, n = n, m
        a, b = b, a
    result = [1] * (m * n)
    for i in range(m):
        for j in range(n):
            result[i * n + j] = int(math.pow(a, i) * math.pow(b, j))
    return result

def print_polynomial(p):
    print(' '.join(map(str, p)))

def main():
    while True:
        a, m, b, n = map(int, input().split())
        if a == 0 and b == 0 and m == 0 and n == 0:
            break
        p = minimal_polynomial(a, m, b, n)
        print_polynomial(p)

if __name__ == "__main__":
    main()
