#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX_N 100
#define MAX_W 50

int main() {
    int n, w, i, j, max_val;
    double ink, total_ink;
    int *values;

    while (scanf("%d %d", &n, &w) == 2) {
        if (n == 0 && w == 0) break;

        values = (int *)malloc((n + 1) * sizeof(int));
        for (i = 0; i < n; i++) {
            scanf("%d", &values[i]);
        }
        values[n] = 0;

        max_val = 0;
        for (i = 0; i < n; i++) {
            if (values[i] > max_val) max_val = values[i];
        }

        ink = 0;
        for (i = 0; i < n; i++) {
            for (j = 0; j < values[i]; j++) {
                int interval = (values[i] - 1) / w + 1;
                double darkness = 1.0 - (double)interval / (max_val / w + 1);
                ink += (double)w * darkness;
            }
        }

        total_ink = ink + 0.01;
        printf("%.5f\n", total_ink);

        free(values);
    }

    return 0;
}
