#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

int main() {
    double totalInk = 0.01;
    while (true) {
        int n, w;
        std::cin >> n >> w;
        if (n == 0 && w == 0) break;

        std::vector<int> values(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> values[i];
        }

        std::vector<int> counts(w, 0);
        for (int value : values) {
            counts[value / w]++;
        }

        double maxCount = *std::max_element(counts.begin(), counts.end());
        double totalArea = 0;
        for (int i = 0; i < counts.size(); ++i) {
            double area = (double)counts[i] / maxCount * w;
            double darkness = 1 - (double)i / (counts.size() - 1);
            totalArea += area * darkness;
        }

        totalInk += totalArea;
    }

    std::cout << std::fixed << std::setprecision(10) << totalInk << std::endl;

    return 0;
}
