import sys

def print_table(table):
    max_width = max(len(str(x)) for row in table for x in row)
    for row in table:
        for i, x in enumerate(row):
            print(f"{x:>{max_width}}", end="")
            if i < len(row) - 1:
                print(" ", end="")
        print(" ", end="")
        for i, x in enumerate([sum(row)]):
            print(f"{x:>{max_width}}", end="")
            if i < len(row) - 1:
                print(" ", end="")
        print()
    print()

def main():
    for line in sys.stdin:
        line = line.strip()
        if line == "0":
            break
        n = int(line)
        table = []
        for _ in range(n):
            row = list(map(int, input().split()))
            table.append(row)
        table.append([sum(row) for row in table])
        table.append([sum(col) for col in zip(*table)])
        print_table(table)

if __name__ == "__main__":
    main()
