import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            int w = scanner.nextInt();
            double total = 0;
            double max = 0;
            for (int i = 0; i < n; i++) {
                int v = scanner.nextInt();
                max = Math.max(max, v);
                total++;
            }
            double base = (max / w) + 1;
            double baseInk = base * 0.01;
            double barInk = 0;
            double ratio = 1.0;
            for (int i = 0; i < base; i++) {
                if (i == 0) {
                    barInk += 1;
                } else if (i == (int) base - 1) {
                    barInk += 0;
                } else {
                    barInk += (1 - (i / base)) * 1;
                }
                ratio *= (1 - (i / base));
            }
            double totalInk = baseInk + (total / base) * barInk * ratio;
            System.out.printf("%.5f%n", totalInk);
            if (n == 0) {
                break;
            }
        }
    }
}
