#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX 1000000000

int main() {
    double prob[10];
    double sum;
    int n, m, k, i, j, num;

    while (scanf("%d %d %d", &n, &m, &k) == 3) {
        if (n == 0 && m == 0 && k == 0) break;

        for (i = 0; i < 10; i++) {
            prob[i] = 0;
        }

        for (i = 0; i < n; i++) {
            for (j = 1; j <= m; j++) {
                for (num = 1; num <= m; num++) {
                    if (i == 0 && k >= num) continue;
                    prob[k - num + 1] += 1.0 / (m * n);
                }
            }
        }

        sum = 0;
        for (i = 1; i <= k; i++) {
            sum += prob[i] * i;
        }
        for (i = k + 1; i <= m * n; i++) {
            sum += prob[i] * (k + 1);
        }

        printf("%.10f\n", sum);
    }

    return 0;
}
