#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

typedef long long ll;

const int N = 1e5 + 5;

struct Watch {
    int h, m, s;
    Watch() {}
    Watch(int h, int m, int s) : h(h), m(m), s(s) {}
};

int gcd(int a, int b) {
    return b ? gcd(b, a % b) : a;
}

int main() {
    int T;
    cin >> T;
    while (T--) {
        int n;
        cin >> n;
        vector<Watch> w(n);
        for (int i = 0; i < n; i++) {
            cin >> w[i].h >> w[i].m >> w[i].s;
        }
        int g = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                g = gcd(g, abs(w[i].h - w[j].h) % 12 * 60 + abs(w[i].m - w[j].m) % 60 + abs(w[i].s - w[j].s) % 60);
            }
        }
        int t = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 60; j++) {
                for (int k = 0; k < 60; k++) {
                    for (int l = 0; l < 60; l++) {
                        if ((abs(w[i].h - (t / 3600 + j)) % 12 * 60 + abs(w[i].m - (t / 60 % 60 + k)) % 60 + abs(w[i].s - (t % 60 + l)) % 60) % g == 0) {
                            int h = t / 3600 + j;
                            int m = t / 60 % 60 + k;
                            int s = t % 60 + l;
                            if (h == 0) {
                                cout << "00:00:00 ";
                            } else {
