                                }
                                cout << s;
                            }
                            h = (t / 3600 + j + 12) % 12;
                            m = (t / 60 % 60 + k);
                            s = (t % 60 + l);
                            if (h == 0) {
                                cout << " 00:00:00";
                            } else {
                                cout << " ";
                                if (h < 10) {
                                    cout << "0";
                                }
                                cout << h << ":";
                                if (m < 10) {
                                    cout << "0";
                                }
                                cout << m << ":";
                                if (s < 10) {
                                    cout << "0";
                                }
                                cout << s;
                            }
                            cout << endl;
                            goto end;
                        }
                    }
                }
            }
        }
        end:;
    }
    return 0;
}
