#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    int n;
    scanf("%d", &n);

    for (int i = 0; i < n; i++) {
        int score = 0;
        int outs = 0;
        int runners[3] = {0, 0, 0};

        int event_count;
        scanf("%d", &event_count);

        for (int j = 0; j < event_count; j++) {
            char event[10];
            scanf("%s", event);

            if (strcmp(event, "HIT") == 0) {
                for (int k = 2; k >= 0; k--) {
                    if (runners[k] == 1) {
                        runners[k] = 0;
                        runners[k - 1] = 1;
                    }
                }
                runners[0] = 1;
            } else if (strcmp(event, "HOMERUN") == 0) {
                score += runners[0] + 1;
                for (int k = 0; k < 3; k++) {
                    runners[k] = 1;
                }
            } else if (strcmp(event, "OUT") == 0) {
                outs++;
            }
        }

        if (outs == 3) {
            score = 0;
        }

        printf("%d\n", score);
    }

    return 0;
}
