#include <iostream>
#include <vector>
#include <string>

int simulateInning(const std::vector<std::string>& events) {
    int score = 0;
    int outs = 0;
    int runners[3] = {0, 0, 0};

    for (const auto& event : events) {
        if (event == "HIT") {
            runners[0]++;
        } else if (event == "HOMERUN") {
            score += runners[0] + 1;
            runners[0] = 0;
        } else if (event == "OUT") {
            outs++;
            if (outs == 3) {
                break;
            }
        }
    }

    return score;
}

int main() {
    int n;
    std::cin >> n;

    for (int i = 0; i < n; i++) {
        int score = 0;
        int outs = 0;
        int runners[3] = {0, 0, 0};
        std::vector<std::string> events;

        int numEvents;
        std::cin >> numEvents;
        for (int j = 0; j < numEvents; j++) {
            std::string event;
            std::cin >> event;
            events.push_back(event);
        }

        score = simulateInning(events);

        std::cout << score << std::endl;
    }

    return 0;
}
