import java.util.*;
import java.io.*;

public class Main {
    static int[][] dir = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
    static int[][] color = {{0, 1, 2, 3, 4, 5}, {1, 0, 3, 5, 4, 2}, {2, 3, 0, 5, 1, 4}, {3, 5, 2, 0, 4, 1}, {4, 2, 5, 1, 0, 3}, {5, 4, 1, 2, 3, 0}};

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        String[] input = br.readLine().split(" ");
        while (!input[0].equals("0") && !input[1].equals("0")) {
            input = br.readLine().split(" ");
            int w = Integer.parseInt(input[0]);
            int d = Integer.parseInt(input[1]);
            char[][] grid = new char[d][w];
            for (int i = 0; i < d; i++) {
                grid[i] = br.readLine().toCharArray();
            }
            String v = br.readLine();
            boolean[][] visited = new boolean[d][w];
            int[][] dist = new int[d][w];
            for (int i = 0; i < d; i++) {
                Arrays.fill(dist[i], -1);
            }
            Queue<int[]> q = new LinkedList<>();
            for (int i = 0; i < d; i++) {
                for (int j = 0; j < w; j++) {
                    if (grid[i][j] == '#') {
                        q.add(new int[]{i, j, 0});
                        dist[i][j] = 0;
                        visited[i][j] = true;
                    }
                }
            }
            while (!q.isEmpty()) {
                int[] cur = q.poll();
                int x = cur[0];
                int y = cur[1];
                int step = cur[2];
                for (int[] d : dir) {
                    int nx = x + d[0];
                    int ny = y + d[1];
                    if (nx >= 0 && nx < d && ny >= 0 && ny < w && !visited[nx][ny]) {
                        if (grid[nx][ny] != 'k' && grid[nx][ny] != 'b') {
                            if (grid[nx][ny] == v.charAt(color[Integer.parseInt(v.substring(0, 1), 6)][Integer.parseInt(v.substring(1, 2), 6)]) || grid[nx][ny] == 'w') {
                                q.add(new int[]{nx, ny, step + 1});
                                dist[nx][ny] = step + 1;
                                visited[nx][ny] = true;
                            }
                        }
                    }
                }
            }
            boolean found = false;
            for (int i = 0; i < d; i++) {
                for (int j = 0; j < w; j++) {
                    if (grid[i][j] == v.charAt(6) && dist[i][j] != -1) {
                        pw.println(dist[i][j]);
                        found = true;
                        break;
                    }
                }
                if (found) break;
            }
            if (!found) pw.println("unreachable");
        }
        pw.close();
    }
}
