from collections import deque

def bfs(grid, order):
    w, d = len(grid[0]), len(grid)
    visited = [[False]*w for _ in range(d)]
    queue = deque([(0, 0, 0, 0, 0, 0, 0)])  # x, y, step, r, g, b, c
    visited[0][0] = True

    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    while queue:
        x, y, step, r, g, b, c = queue.popleft()

        if (r, g, b, c) == tuple(order):
            return step

        for dx, dy in directions:
            nx, ny = x + dx, y + dy

            if 0 <= nx < w and 0 <= ny < d and not visited[ny][nx]:
                visited[ny][nx] = True

                if grid[ny][nx] in 'rgbcmy':
                    if grid[ny][nx] == order[0]:
                        queue.append((nx, ny, step + 1, 0, g, b, c))
                    elif grid[ny][nx] == order[1]:
                        queue.append((nx, ny, step + 1, r, 0, b, c))
                    elif grid[ny][nx] == order[2]:
                        queue.append((nx, ny, step + 1, r, g, 0, c))
                    elif grid[ny][nx] == order[3]:
                        queue.append((nx, ny, step + 1, r, g, b, 0))
                    elif grid[ny][nx] == order[4]:
                        queue.append((nx, ny, step + 1, r, g, b, c))
                    elif grid[ny][nx] == order[5]:
                        queue.append((nx, ny, step + 1, r, g, b, c))
                elif grid[ny][nx] == '#':
                    queue.append((nx, ny, step + 1, r, g, b, c))

    return -1

def main():
    while True:
        w, d = map(int, input().split())
        if w == 0 and d == 0:
            break

        grid = []
        for _ in range(d):
            grid.append(input())

        order = input()
        print(bfs(grid, order) if bfs(grid, order) != -1 else 'unreachable')

if __name__ == '__main__':
    main()
