#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int MAX_N = 12;
const int MAX_M = 5000;
const int MAX_E = 20;
const int MAX_T = 100;

int kmp(const string& s, const vector<string>& p) {
    int n = s.size();
    int m = p.size();
    vector<int> pi(m + 1);
    for (int i = 1; i <= m; ++i) {
        int j = pi[i - 1];
        while (j > 0 && p[i - 1] != p[j - 1]) {
            j = pi[j - 1];
        }
        if (p[i - 1] == p[j - 1]) {
            ++j;
        }
        pi[i] = j;
    }
    int res = 0;
    for (int i = 0; i <= n; ++i) {
        int j = 0;
        while (j < m && i + j < n && s[i + j] == p[j]) {
            ++j;
        }
        if (j > 0) {
            res += (pi[j] == j) ? 1 : (n - i - j + 1) / (j - pi[j]) + 1;
        }
    }
    return res;
}

int main() {
    int n, m;
    while (cin >> n >> m) {
        if (n == 0 && m == 0) {
            break;
        }
        vector<string> p(n);
        for (int i = 0; i < n; ++i) {
            cin >> p[i];
        }
        string t;
        for (int i = 0; i < m; ++i) {
            string s;
            getline(cin, s);
            t += s;
        }
        cout << kmp(t, p) << endl;
    }
    return 0;
}
