import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = br.readLine()) != null) {
            if (line.equals("0 0")) break;
            String[] parts = line.split(" ");
            int n = Integer.parseInt(parts[0]);
            int m = Integer.parseInt(parts[1]);
            List<String> e = new ArrayList<>();
            for (int i = 0; i < n; i++) {
                e.add(br.readLine());
            }
            List<String> t = new ArrayList<>();
            for (int i = 0; i < m; i++) {
                t.add(br.readLine());
            }
            System.out.println(search(e, t));
        }
    }

    public static int search(List<String> e, List<String> t) {
        int n = e.size();
        int m = t.size();
        int[][] p = new int[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                p[i][j] = kmp(e.get(i), e.get(j));
            }
        }
        int[][] dp = new int[1 << n][m];
        for (int i = 0; i < (1 << n); i++) {
            for (int j = 0; j < m; j++) {
                dp[i][j] = -1;
            }
        }
        dp[0][0] = 0;
        for (int i = 0; i < (1 << n); i++) {
            for (int j = 0; j < m; j++) {
                if (dp[i][j] == -1) continue;
                for (int k = 0; k < n; k++) {
                    if ((i & (1 << k)) == 0) {
                        int next = i | (1 << k);
                        int nextJ = j + 1;
                        if (nextJ < m && dp[next][nextJ] == -1) {
                            int match = Math.max(0, dp[i][j] - e.get(k).length() + 1);
                            int pos = t.get(nextJ).indexOf(e.get(k), match);
                            if (pos != -1) {
                                dp[next][nextJ] = pos + 1;
                            }
                        }
                        if (nextJ < m && dp[next][nextJ] != -1) {
                            int match = Math.max(0, dp[i][j] - e.get(k).length() + 1);
                            int pos = t.get(nextJ).indexOf(e.get(k), match);
                            if (pos != -1) {
                                dp[next][nextJ] = Math.max(dp[next][nextJ], pos + 1);
                            }
                        }
                    }
                }
            }
        }
        int max = 0;
        for (int i = 0; i < (1 << n); i++) {
            for (int j = 0; j < m; j++) {
                if (dp[i][j] != -1) {
                    max = Math.max(max, dp[i][j]);
                }
            }
        }
        return max;
    }

    public static int kmp(String s, String p) {
        int[] pi = new int[p.length()];
        int j = 0;
        for (int i = 1; i < p.length(); i++) {
            while (j > 0 && p.charAt(i) != p.charAt(j)) {
                j = pi[j - 1];
            }
            if (p.charAt(i) == p.charAt(j)) {
                j++;
            }
            pi[i] = j;
        }
        j = 0;
        for (int i = 0; i < s.length(); i++) {
            while (j > 0 && s.charAt(i) != p.charAt(j)) {
                j = pi[j - 1];
            }
            if (s.charAt(i) == p.charAt(j)) {
                j++;
            }
            if (j == p.length()) {
                return i - p.length() + 1;
            }
        }
        return -1;
    }
}
