#include <stdio.h>
#include <math.h>

#define MAX_POINTS 10
#define MAX_SEGMENTS 5

typedef struct {
    double x, y;
} Point;

typedef struct {
    int n;
    Point points[MAX_POINTS];
} Dataset;

double distance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double zigzag_length(Dataset dataset, int turns) {
    double length = 0;
    for (int i = 0; i < turns; i++) {
        length += distance(dataset.points[i], dataset.points[i + 1]);
    }
    return length;
}

int main() {
    int n;
    Dataset datasets[10];
    int num_datasets = 0;

    while (scanf("%d", &n) == 1) {
        if (n == 0) break;

        for (int i = 0; i < n; i++) {
            scanf("%lf %lf", &datasets[num_datasets].points[i].x, &datasets[num_datasets].points[i].y);
        }

        datasets[num_datasets].n = n;

        double min_length = 1e30;
        int min_turns = 0;

        for (int turns = 0; turns <= 4; turns++) {
            double length = zigzag_length(datasets[num_datasets], turns);
            if (length < min_length) {
                min_length = length;
                min_turns = turns;
            }
        }

        printf("%d %.5lf\n", min_turns, min_length);

        num_datasets++;
    }

    return 0;
}
