#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

using namespace std;

double distance(pair<int, int> p1, pair<int, int> p2) {
    return sqrt(pow(p1.first - p2.first, 2) + pow(p1.second - p2.second, 2));
}

double zigzag(vector<pair<int, int>> points, int turns) {
    if (turns == 0) return 0;
    double total = 0;
    for (int i = 0; i < points.size() - 1; i++) {
        total += distance(points[i], points[i + 1]);
    }
    for (int i = 0; i < turns; i++) {
        total += distance(points[i], points[i + 1]);
    }
    return total;
}

void solve() {
    int n;
    cin >> n;
    vector<pair<int, int>> points(n);
    for (int i = 0; i < n; i++) {
        cin >> points[i].first >> points[i].second;
    }
    double minTurns = 1e9;
    double minLength = 1e9;
    for (int turns = 0; turns <= 4; turns++) {
        double length = zigzag(points, turns);
        if (length < minLength || (length == minLength && turns < minTurns)) {
            minLength = length;
            minTurns = turns;
        }
    }
    cout << minTurns << " " << minLength << endl;
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        solve();
    }
    return 0;
}
