import java.util.*;
import java.io.*;

public class Main {
    static class Point {
        double x, y;
        Point(double x, double y) {
            this.x = x;
            this.y = y;
        }
    }

    static double dist(Point a, Point b) {
        return Math.sqrt(Math.pow(a.x - b.x, 2) + Math.pow(a.y - b.y, 2));
    }

    static double zigzag(Point[] points, int turns) {
        double total = 0;
        for (int i = 0; i < turns; i++) {
            total += dist(points[i], points[i + 1]);
        }
        for (int i = 0; i < points.length - turns; i++) {
            total += dist(points[i], points[i + turns]);
        }
        return total;
    }

    static double minZigzag(Point[] points) {
        double min = Double.MAX_VALUE;
        for (int turns = 0; turns <= 4; turns++) {
            double total = zigzag(points, turns);
            if (total < min) {
                min = total;
            }
        }
        return min;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int T = Integer.parseInt(br.readLine());
        while (T-- > 0) {
            int n = Integer.parseInt(br.readLine());
            Point[] points = new Point[n];
            StringTokenizer st = new StringTokenizer(br.readLine());
            for (int i = 0; i < n; i++) {
                points[i] = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
            }
            double min = minZigzag(points);
            int turns = 0;
            for (int i = 0; i <= 4; i++) {
                if (Math.abs(zigzag(points, i) - min) < 1e-4) {
                    turns = i;
                    break;
                }
            }
            System.out.printf("%d %.5f\n", turns, min);
        }
    }
}
