import math
import itertools

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def zigzag_length(points, turns):
    if len(turns) == 0:
        return 0
    elif len(turns) == 1:
        return distance(points[turns[0]], points[turns[1]])
    else:
        return distance(points[turns[0]], points[turns[1]]) + \
               sum(distance(points[i], points[i+1]) for i in range(1, len(turns)-1)) + \
               distance(points[turns[-1]], points[turns[0]])

def solve(points):
    min_turns = float('inf')
    min_length = float('inf')
    for r in range(1, len(points)+1):
        for turns in itertools.combinations(range(len(points)), r):
            length = zigzag_length(points, turns)
            if len(turns) < min_turns or (len(turns) == min_turns and length < min_length):
                min_turns = len(turns)
                min_length = length
    return min_turns, min_length

def main():
    while True:
        n = int(input())
        points = []
        for _ in range(n):
            x, y = map(int, input().split())
            points.append((x, y))
        turns, length = solve(points)
        print(f"{turns} {length:.7f}")

        if n == 0:
            break

if __name__ == "__main__":
    main()
