#include <stdio.h>
#include <stdlib.h>

#define MAX_W 11
#define MAX_D 11
#define MAX_H 21

int min_cubes(int w, int d, int h[], int h_prime[]) {
    int dp[w + 1][d + 1][MAX_H + 1];
    for (int i = 0; i <= w; i++) {
        for (int j = 0; j <= d; j++) {
            for (int k = 0; k <= MAX_H; k++) {
                dp[i][j][k] = 0;
            }
        }
    }

    for (int i = 1; i <= w; i++) {
        for (int k = 1; k <= MAX_H; k++) {
            dp[i][0][k] = dp[i - 1][0][k - 1] + h[i - 1];
        }
    }

    for (int j = 1; j <= d; j++) {
        for (int k = 1; k <= MAX_H; k++) {
            dp[0][j][k] = dp[0][j - 1][k - 1] + h_prime[j - 1];
        }
    }

    for (int i = 1; i <= w; i++) {
        for (int j = 1; j <= d; j++) {
            for (int k = 1; k <= MAX_H; k++) {
                dp[i][j][k] = dp[i - 1][j][k - 1] + dp[i][j - 1][k - 1] + h[i - 1] + h_prime[j - 1] - k + 1;
                if (h[i - 1] < k) {
                    dp[i][j][k] = dp[i][j][k] - h[i - 1] + k;
                }
                if (h_prime[j - 1] < k) {
                    dp[i][j][k] = dp[i][j][k] - h_prime[j - 1] + k;
                }
            }
        }
    }

    int min = MAX_H + 1;
    for (int k = 1; k <= MAX_H; k++) {
        if (dp[w][d][k] < min) {
            min = dp[w][d][k];
        }
    }

    return min;
}

int main() {
    int w, d;
    int h[MAX_W], h_prime[MAX_D];

    while (scanf("%d %d", &w, &d) == 2) {
        if (w == 0 && d == 0) {
            break;
        }

        for (int i = 0; i < w; i++) {
            scanf("%d", &h[i]);
        }

        for (int i = 0; i < d; i++) {
            scanf("%d", &h_prime[i]);
        }

        printf("%d\n", min_cubes(w, d, h, h_prime));
    }

    return 0;
}
