#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int w, d;
    while (cin >> w >> d) {
        if (w == 0 && d == 0) break;
        vector<int> h(w), h_(d);
        for (int i = 0; i < w; ++i) cin >> h[i];
        for (int i = 0; i < d; ++i) cin >> h_[i];

        vector<int> dp(w + 1, 1e9);
        dp[0] = 0;
        for (int i = 0; i < w; ++i) {
            for (int j = i; j >= 0; --j) {
                dp[j + 1] = min(dp[j + 1], dp[j] + h[i]);
            }
        }

        vector<int> dp_(d + 1, 1e9);
        dp_[0] = 0;
        for (int i = 0; i < d; ++i) {
            for (int j = i; j >= 0; --j) {
                dp_[j + 1] = min(dp_[j + 1], dp_[j] + h_[i]);
            }
        }

        int ans = 1e9;
        for (int i = 0; i <= w; ++i) {
            for (int j = 0; j <= d; ++j) {
                ans = min(ans, dp[i] + dp_[j] + max(i, j));
            }
        }

        cout << ans << endl;
    }

    return 0;
}
