import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int w = scanner.nextInt();
            int d = scanner.nextInt();
            if (w == 0 && d == 0) {
                break;
            }
            int[] front = new int[w];
            int[] side = new int[d];
            for (int i = 0; i < w; i++) {
                front[i] = scanner.nextInt();
            }
            for (int i = 0; i < d; i++) {
                side[i] = scanner.nextInt();
            }
            int[][] dp = new int[w + 1][d + 1];
            for (int i = 0; i <= w; i++) {
                for (int j = 0; j <= d; j++) {
                    dp[i][j] = Integer.MAX_VALUE;
                }
            }
            dp[0][0] = 0;
            for (int i = 1; i <= w; i++) {
                for (int j = 0; j <= d; j++) {
                    dp[i][j] = Math.min(dp[i][j], dp[i - 1][j] + front[i - 1]);
                }
            }
            for (int i = 1; i <= d; i++) {
                for (int j = 1; j <= w; j++) {
                    dp[j][i] = Math.min(dp[j][i], dp[j][i - 1] + side[i - 1]);
                }
            }
            System.out.println(dp[w][d]);
        }
    }
}
