#include <stdio.h>
#include <string.h>
#include <stdbool.h>

#define MAXN 20
#define MAXM 20

int n;
char alpha[MAXN][MAXM], beta[MAXN][MAXM];
char gamma[MAXM], delta[MAXM];

bool check(char *s, char *t) {
    int i, j, k;
    for (i = 0; i < strlen(s); i++) {
        for (j = 0; j < strlen(t); j++) {
            k = 0;
            while (i + k < strlen(s) && j + k < strlen(t) && s[i + k] == t[j + k]) k++;
            if (k > 0 && k < strlen(t)) return false;
        }
    }
    return true;
}

int main() {
    int T;
    scanf("%d", &T);
    while (T--) {
        scanf("%d", &n);
        for (int i = 0; i < n; i++) {
            scanf("%s %s", alpha[i], beta[i]);
        }
        scanf("%s %s", gamma, delta);
        int ans = 0;
        bool flag = true;
        while (flag) {
            flag = false;
            for (int i = 0; i < n; i++) {
                if (check(gamma, alpha[i])) {
                    int j;
                    for (j = 0; j < strlen(gamma); j++) {
                        if (j + strlen(alpha[i]) <= strlen(gamma) && memcmp(gamma + j, alpha[i], strlen(alpha[i])) == 0) {
                            for (int k = 0; k < strlen(beta[i]); k++) {
                                gamma[j + k] = beta[i][k];
                            }
                            flag = true;
                            break;
                        }
                    }
                    if (flag) break;
                }
            }
            if (flag) ans++;
        }
        if (strcmp(gamma, delta) == 0) printf("%d\n", ans);
        else printf("-1\n");
    }
    return 0;
}
