#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int minSubstitutions(string s, vector<pair<string, string>> pairs) {
    int n = pairs.size();
    int count = 0;
    bool found = true;

    while (found) {
        found = false;
        for (int i = 0; i < n; i++) {
            string alpha = pairs[i].first;
            string beta = pairs[i].second;
            size_t pos = s.find(alpha);
            while (pos != string::npos) {
                s.replace(pos, alpha.length(), beta);
                pos = s.find(alpha, pos + beta.length());
                found = true;
            }
        }
        count++;
    }

    if (s == "a" || s == "b" || s == "c" || s == "d" || s == "e" || s == "f" || s == "g" || s == "h" || s == "i" || s == "j") {
        return -1;
    }

    return count - 1;
}

int main() {
    int t;
    cin >> t;
    cin.ignore();

    for (int i = 0; i < t; i++) {
        int n;
        cin >> n;
        cin.ignore();

        vector<pair<string, string>> pairs;
        for (int j = 0; j < n; j++) {
            string alpha, beta;
            getline(cin, alpha);
            getline(cin, beta);
            pairs.push_back(make_pair(alpha, beta));
        }

        string gamma, delta;
        getline(cin, gamma);
        getline(cin, delta);

        cout << minSubstitutions(delta, pairs) << endl;
    }

    return 0;
}
