import java.util.*;
import java.io.*;

public class Main {
    static class Pair {
        String a;
        String b;

        public Pair(String a, String b) {
            this.a = a;
            this.b = b;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int t = Integer.parseInt(br.readLine());
        while (t-- > 0) {
            int n = Integer.parseInt(br.readLine());
            List<Pair> pairs = new ArrayList<>();
            for (int i = 0; i < n; i++) {
                String[] input = br.readLine().split(" ");
                pairs.add(new Pair(input[0], input[1]));
            }
            String gamma = br.readLine();
            String delta = br.readLine();
            if (gamma.equals(delta)) {
                System.out.println(0);
                continue;
            }
            int count = 0;
            boolean flag = true;
            while (flag) {
                flag = false;
                for (Pair pair : pairs) {
                    int index = gamma.indexOf(pair.a);
                    while (index != -1) {
                        if (gamma.substring(index, index + pair.a.length()).equals(pair.a)) {
                            String temp = gamma.substring(0, index) + pair.b + gamma.substring(index + pair.a.length());
                            if (temp.equals(delta)) {
                                count++;
                                gamma = temp;
                                flag = true;
                            }
                            gamma = gamma.substring(0, index) + gamma.substring(index + pair.a.length());
                        }
                        index = gamma.indexOf(pair.a, index + 1);
                    }
                }
            }
            if (gamma.equals(delta)) {
                System.out.println(count);
            } else {
                System.out.println(-1);
            }
        }
    }
}
