import re

def min_substitutions(n, pairs, gamma, delta):
    substitutions = {}
    for pair in pairs:
        alpha, beta = pair.split()
        substitutions[alpha] = beta

    def substitute(match):
        return re.sub(r'\1+' + alpha, beta, match.group(0))

    def substitute_all(string):
        for alpha, beta in substitutions.items():
            string = re.sub(alpha, beta, string)
        return string

    def is_possible(delta):
        return substitute_all(gamma) == delta

    def dfs(current, count):
        if current == delta:
            return count
        if not is_possible(current):
            return -1
        for alpha, beta in substitutions.items():
            new_current = re.sub(alpha, beta, current)
            result = dfs(new_current, count + 1)
            if result != -1:
                return result
        return -1

    return dfs(gamma, 0)

def main():
    while True:
        n = int(input())
        pairs = []
        for _ in range(n):
            pairs.append(input())
        gamma = input()
        delta = input()
        if delta == '0':
            break
        print(min_substitutions(n, pairs, gamma, delta))

if __name__ == "__main__":
    main()
