#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int pace;
    int laps;
    int time;
    int end_time;
} Swimmer;

int compare(const void *a, const void *b) {
    Swimmer *swimmer_a = (Swimmer *)a;
    Swimmer *swimmer_b = (Swimmer *)b;
    return (swimmer_b->pace - swimmer_a->pace);
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        Swimmer *swimmers = (Swimmer *)malloc(n * sizeof(Swimmer));
        for (int i = 0; i < n; i++) {
            scanf("%d %d", &swimmers[i].pace, &swimmers[i].laps);
            swimmers[i].time = swimmers[i].pace * (swimmers[i].laps * 2);
            swimmers[i].end_time = swimmers[i].time;
        }
        qsort(swimmers, n, sizeof(Swimmer), compare);
        int time = 0;
        for (int i = 0; i < n; i++) {
            if (swimmers[i].end_time > time) {
                time = swimmers[i].end_time;
            }
        }
        printf("%d\n", time);
        free(swimmers);
    }
    return 0;
}
