#include <iostream>
#include <queue>
#include <vector>
#include <algorithm>

using namespace std;

struct Swimmer {
    int pace;
    int laps;
    int time;
    int end;
};

bool compare(Swimmer a, Swimmer b) {
    return a.pace < b.pace;
}

int main() {
    int n;
    while (cin >> n) {
        if (n == 0) break;
        vector<Swimmer> swimmers(n);
        for (int i = 0; i < n; i++) {
            cin >> swimmers[i].pace >> swimmers[i].laps;
            swimmers[i].time = swimmers[i].pace * swimmers[i].laps * 2;
            swimmers[i].end = swimmers[i].time;
        }
        sort(swimmers.begin(), swimmers.end(), compare);
        priority_queue<int, vector<int>, greater<int>> queue;
        for (int i = 0; i < n; i++) {
            queue.push(swimmers[i].pace);
        }
        int time = 0;
        while (!queue.empty()) {
            int pace = queue.top();
            queue.pop();
            if (pace <= time) {
                continue;
            }
            time = pace;
            for (int i = 0; i < n; i++) {
                if (swimmers[i].pace == pace) {
                    swimmers[i].end = time;
                    queue.push(swimmers[i].pace + swimmers[i].pace);
                }
            }
        }
        int maxTime = 0;
        for (int i = 0; i < n; i++) {
            maxTime = max(maxTime, swimmers[i].end);
        }
        cout << maxTime << endl;
    }
    return 0;
}
