import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextInt()) {
            int n = scanner.nextInt();
            int[][] swimmers = new int[n][2];
            for (int i = 0; i < n; i++) {
                swimmers[i][0] = scanner.nextInt();
                swimmers[i][1] = scanner.nextInt();
            }
            int time = 0;
            while (true) {
                Arrays.sort(swimmers, (a, b) -> a[0] * (a[1] + 1) - b[0] * (b[1] + 1));
                int maxTime = 0;
                for (int i = 0; i < n; i++) {
                    maxTime = Math.max(maxTime, swimmers[i][0] * (swimmers[i][1] + 1));
                }
                time += maxTime;
                int[] newSwimmers = new int[n];
                int j = 0;
                for (int i = 0; i < n; i++) {
                    if (swimmers[i][0] * (swimmers[i][1] + 1) == maxTime) {
                        newSwimmers[j++] = swimmers[i][0];
                        newSwimmers[j++] = swimmers[i][1] + 1;
                    }
                }
                n = j;
                if (n == 0) break;
                swimmers = new int[n][2];
                System.arraycopy(newSwimmers, 0, swimmers, 0, n);
            }
            System.out.println(time);
        }
    }
}
