#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_H 100
#define MAX_W 100

typedef struct {
    int x, y;
} Position;

int getDirection(char c) {
    switch (c) {
        case '>':
            return 0;
        case '<':
            return 1;
        case '^':
            return 2;
        case 'v':
            return 3;
        default:
            return -1;
    }
}

int isValidPosition(int h, int w, int x, int y) {
    return (x >= 0 && x < w && y >= 0 && y < h);
}

int main() {
    int h, w;
    char grid[MAX_H][MAX_W + 1];
    Position position = {0, 0};
    int visited[MAX_H][MAX_W];

    while (scanf("%d %d", &h, &w) == 2) {
        if (h == 0 && w == 0) break;

        for (int i = 0; i < h; i++) {
            scanf("%s", grid[i]);
            for (int j = 0; j < w; j++) {
                visited[i][j] = 0;
            }
        }

        int direction = -1;
        int steps = 0;
        while (1) {
            if (visited[position.y][position.x]) {
                printf("LOOP\n");
                break;
            }

            visited[position.y][position.x] = 1;

            int newDirection = getDirection(grid[position.y][position.x]);
            if (newDirection == -1) {
                break;
            }

            int newDirectionValue = (direction + newDirection + 4) % 4;
            switch (newDirectionValue) {
                case 0:
                    position.x++;
                    break;
                case 1:
                    position.x--;
                    break;
                case 2:
                    position.y--;
                    break;
                case 3:
                    position.y++;
                    break;
            }

            if (!isValidPosition(h, w, position.x, position.y)) {
                printf("LOOP\n");
                break;
            }

            direction = newDirection;
            steps++;
        }

        printf("%d %d\n", position.x, position.y);
    }

    return 0;
}
