#include <iostream>
#include <vector>
#include <algorithm>

struct Point {
    int x, y;
};

bool comparePoints(const Point& a, const Point& b) {
    return a.x < b.x || (a.x == b.x && a.y < b.y);
}

bool checkLine(const std::vector<Point>& black, const std::vector<Point>& white) {
    for (int i = 0; i < black.size(); ++i) {
        for (int j = 0; j < white.size(); ++j) {
            int x1 = black[i].x, y1 = black[i].y;
            int x2 = white[j].x, y2 = white[j].y;
            int x3 = black[(i + 1) % black.size()].x, y3 = black[(i + 1) % black.size()].y;
            int x4 = white[(j + 1) % white.size()].x, y4 = white[(j + 1) % white.size()].y;

            int denominator = (x3 - x1) * (y4 - y2) - (y3 - y1) * (x4 - x2);
            if (denominator == 0) continue;

            int x = (x3 * y4 - y3 * x4) * (x2 - x1) - (x3 - x1) * (x2 * y4 - y2 * x4);
            int y = (x3 * y4 - y3 * x4) * (y2 - y1) - (y3 - y1) * (x2 * y4 - y2 * x4);

            if (x >= 0 && x <= 10000 && y >= 0 && y <= 10000) {
                return true;
            }
        }
    }
    return false;
}

int main() {
    int n, m;
    while (std::cin >> n >> m) {
        if (n == 0 && m == 0) break;

        std::vector<Point> black(n), white(m);
        for (int i = 0; i < n; ++i) {
            std::cin >> black[i].x >> black[i].y;
        }
        for (int i = 0; i < m; ++i) {
            std::cin >> white[i].x >> white[i].y;
        }

        std::sort(black.begin(), black.end(), comparePoints);
        std::sort(white.begin(), white.end(), comparePoints);

        if (checkLine(black, white)) {
            std::cout << "YES" << std::endl;
        } else {
            std::cout << "NO" << std::endl;
        }
    }

    return 0;
}
