import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextInt()) {
            int n = scanner.nextInt();
            int m = scanner.nextInt();
            int[][] points = new int[n + m][2];
            for (int i = 0; i < n; i++) {
                points[i][0] = scanner.nextInt();
                points[i][1] = scanner.nextInt();
            }
            for (int i = n; i < n + m; i++) {
                points[i][0] = scanner.nextInt();
                points[i][1] = scanner.nextInt();
            }
            boolean flag = true;
            for (int i = 0; i < n; i++) {
                for (int j = i + 1; j < n; j++) {
                    if (points[i][0] == points[j][0]) {
                        flag = false;
                        break;
                    }
                }
                if (!flag) break;
            }
            if (flag) {
                System.out.println("NO");
                continue;
            }
            flag = true;
            for (int i = n; i < n + m; i++) {
                for (int j = i + 1; j < n + m; j++) {
                    if (points[i][0] == points[j][0]) {
                        flag = false;
                        break;
                    }
                }
                if (!flag) break;
            }
            if (flag) {
                System.out.println("NO");
                continue;
            }
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n + m; j++) {
                    if (points[i][0] < points[j][0] && (points[i][1] - points[j][1]) * (points[n + m - 1][0] - points[j][0]) < 0) {
                        flag = false;
                        break;
                    }
                }
                if (!flag) break;
            }
            if (flag) {
                System.out.println("YES");
            } else {
                System.out.println("NO");
            }
        }
    }
}
