def separate_points():
    while True:
        try:
            n, m = map(int, input().split())
            points = []
            for _ in range(n + m):
                x, y = map(int, input().split())
                points.append((x, y))
            if n == 0 or m == 0:
                print("YES")
                continue
            points.sort(key=lambda x: x[0])
            slope = None
            for i in range(n + m - 1):
                x1, y1 = points[i]
                x2, y2 = points[i + 1]
                if y2 - y1 != 0:
                    slope = (y2 - y1) / (x2 - x1)
                    break
            if slope is None:
                print("NO")
                continue
            for i in range(n + m):
                x, y = points[i]
                if (y - points[0][1]) * slope != x - points[0][0]:
                    print("NO")
                    break
            else:
                print("YES")
        except ValueError:
            break

