#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int h, w;
    while (cin >> h >> w) {
        if (h == 0 && w == 0) break;
        vector<string> room(h);
        for (int i = 0; i < h; i++) {
            cin >> room[i];
        }
        int x = 0, y = 0;
        while (true) {
            if (room[y][x] == '.') {
                cout << x << " " << y << endl;
                break;
            }
            if (room[y][x] == '^') {
                if (y == 0) {
                    cout << "LOOP" << endl;
                    break;
                }
                if (room[y - 1][x] == '.') {
                    cout << x << " " << y - 1 << endl;
                    break;
                }
                y--;
            } else if (room[y][x] == 'v') {
                if (y == h - 1) {
                    cout << "LOOP" << endl;
                    break;
                }
                if (room[y + 1][x] == '.') {
                    cout << x << " " << y + 1 << endl;
                    break;
                }
                y++;
            } else if (room[y][x] == '<') {
                if (x == 0) {
                    cout << "LOOP" << endl;
                    break;
                }
                if (room[y][x - 1] == '.') {
                    cout << x - 1 << " " << y << endl;
                    break;
                }
                x--;
            } else if (room[y][x] == '>') {
                if (x == w - 1) {
                    cout << "LOOP" << endl;
                    break;
                }
                if (room[y][x + 1] == '.') {
                    cout << x + 1 << " " << y << endl;
                    break;
                }
                x++;
            }
        }
    }
    return 0;
}
