#include <iostream>
#include <cmath>

struct Point {
    double x, y;
};

struct Circle {
    double x, y, r;
};

Circle malfatti_circle(const Point& p1, const Point& p2, const Point& p3) {
    double a = sqrt(pow(p2.x - p3.x, 2) + pow(p2.y - p3.y, 2));
    double b = sqrt(pow(p1.x - p3.x, 2) + pow(p1.y - p3.y, 2));
    double c = sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
    double s = (a + b + c) / 2;
    double area = sqrt(s * (s - a) * (s - b) * (s - c));
    double r1 = (2 * area) / a;
    double r2 = (2 * area) / b;
    double r3 = (2 * area) / c;
    double x1 = (a * p1.x + b * p2.x + c * p3.x) / (a + b + c);
    double y1 = (a * p1.y + b * p2.y + c * p3.y) / (a + b + c);
    double x2 = (a * p3.x + b * p1.x + c * p2.x) / (a + b + c);
    double y2 = (a * p3.y + b * p1.y + c * p2.y) / (a + b + c);
    double x3 = (a * p2.x + b * p3.x + c * p1.x) / (a + b + c);
    double y3 = (a * p2.y + b * p3.y + c * p1.y) / (a + b + c);
    double d1 = sqrt(pow(x1 - p1.x, 2) + pow(y1 - p1.y, 2));
    double d2 = sqrt(pow(x2 - p2.x, 2) + pow(y2 - p2.y, 2));
    double d3 = sqrt(pow(x3 - p3.x, 2) + pow(y3 - p3.y, 2));
    if (d1 <= d2 && d1 <= d3) {
        return {x1, y1, r1};
    } else if (d2 <= d1 && d2 <= d3) {
        return {x2, y2, r2};
    } else {
        return {x3, y3, r3};
    }
}

int main() {
    while (true) {
        double x1, y1, x2, y2, x3, y3;
        std::cin >> x1 >> y1 >> x2 >> y2 >> x3 >> y3;
        if (x1 == 0 && y1 == 0 && x2 == 0 && y2 == 0 && x3 == 0 && y3 == 0) {
            break;
        }
        Circle c1 = malfatti_circle({x1, y1}, {x2, y2}, {x3, y3});
        Circle c2 = malfatti_circle({x2, y2}, {x3, y3}, {x1, y1});
        Circle c3 = malfatti_circle({x3, y3}, {x1, y1}, {x2, y2});
        std::cout << std::fixed << std::setprecision(6) << c1.r << " " << c2.r << " " << c3.r << std::endl;
    }
    return 0;
}
