import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            double x1 = scanner.nextDouble();
            double y1 = scanner.nextDouble();
            double x2 = scanner.nextDouble();
            double y2 = scanner.nextDouble();
            double x3 = scanner.nextDouble();
            double y3 = scanner.nextDouble();

            if (x1 == 0 && y1 == 0 && x2 == 0 && y2 == 0 && x3 == 0 && y3 == 0) {
                break;
            }

            double[] r = malfattiCircles(x1, y1, x2, y2, x3, y3);
            System.out.printf("%.6f %.6f %.6f%n", r[0], r[1], r[2]);
        }
    }

    public static double[] malfattiCircles(double x1, double y1, double x2, double y2, double x3, double y3) {
        double[] r = new double[3];

        // Calculate the side lengths of the triangle
        double a = distance(x1, y1, x2, y2);
        double b = distance(x2, y2, x3, y3);
        double c = distance(x3, y3, x1, y1);

        // Calculate the semi-perimeter of the triangle
        double s = (a + b + c) / 2;

        // Calculate the area of the triangle using Heron's formula
        double area = Math.sqrt(s * (s - a) * (s - b) * (s - c));

        // Calculate the inradius of the triangle
        double inradius = area / s;

        // Calculate the radii of the Malfatti circles
        r[0] = (a * inradius) / (a + b + c);
        r[1] = (b * inradius) / (a + b + c);
        r[2] = (c * inradius) / (a + b + c);

        return r;
    }

    public static double distance(double x1, double y1, double x2, double y2) {
        return Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
    }
}
