import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def circumradius(a, b, c):
    s = (a + b + c) / 2
    return math.sqrt((s - a) * (s - b) * (s - c) * (s + a + b + c) / (4 * s))

def malfatti_circle(a, b, c, p):
    r = circumradius(a, b, c)
    return r * math.sqrt((r - a) * (r - b) * (r - c) / (r + a + b + c)) / (a + b + c)

def malfatti_circles(a, b, c):
    r1 = malfatti_circle(a, b, c, a)
    r2 = malfatti_circle(a, b, c, b)
    r3 = malfatti_circle(a, b, c, c)
    return r1, r2, r3

def main():
