#include <stdio.h>
#include <string.h>

#define MAX_M 11
#define MAX_N 128

int min(int a, int b) {
    return (a < b) ? a : b;
}

int max(int a, int b) {
    return (a > b) ? a : b;
}

int min_max(int m, int n, char **objects) {
    int i, j, k, max_q = 0;
    int *features = (int *)malloc(m * sizeof(int));
    int *questions = (int *)malloc(n * sizeof(int));

    for (i = 0; i < m; i++) {
        features[i] = 0;
    }

    for (i = 0; i < n; i++) {
        questions[i] = 0;
    }

    for (i = 0; i < n; i++) {
        for (j = 0; j < m; j++) {
            if (objects[i][j] == '1') {
                features[j] |= (1 << i);
            }
        }
    }

    for (i = 0; i < n; i++) {
        for (j = 0; j < m; j++) {
            if (objects[i][j] == '1') {
                int q = 0;
                for (k = 0; k < n; k++) {
                    if (i != k) {
                        int mask = features[j];
                        int obj = 0;
                        for (int l = 0; l < m; l++) {
                            if (objects[k][l] == '1') {
                                obj |= (1 << l);
                            }
                        }
                        if ((obj & mask) != mask) {
                            q++;
                        }
                    }
                }
                questions[i] = max(questions[i], q);
            }
        }
    }

    for (i = 0; i < n; i++) {
        max_q = max(max_q, questions[i]);
    }

    free(features);
    free(questions);

    return max_q;
}

int main() {
    int m, n;
    char **objects = NULL;
    int i, j;

    while (scanf("%d %d", &m, &n) == 2) {
        if (m == 0 && n == 0) {
            break;
        }

        objects = (char **)malloc(n * sizeof(char *));
        for (i = 0; i < n; i++) {
            objects[i] = (char *)malloc((m + 1) * sizeof(char));
            scanf("%s", objects[i]);
        }

        int max_q = min_max(m, n, objects);
        printf("%d\n", max_q);

        for (i = 0; i < n; i++) {
            free(objects[i]);
        }
        free(objects);
    }

    return 0;
}
