#include <iostream>
#include <vector>
#include <algorithm>
#include <utility>

using namespace std;

const int N = 2e5 + 5;
const int mod = 998244353;

int n, m, cnt, ans;
vector<int> G[N];
int dep[N], fa[N], son[N], siz[N], top[N], dfn[N], id[N], d[N];
int f[N][N], g[N][N];

void dfs1(int u, int p) {
    dep[u] = dep[p] + 1;
    fa[u] = p;
    siz[u] = 1;
    for (int v : G[u]) {
        if (v == p) continue;
        dfs1(v, u);
        siz[u] += siz[v];
        if (siz[v] > siz[son[u]]) son[u] = v;
    }
}

void dfs2(int u, int tp) {
    top[u] = tp;
    id[u] = ++cnt;
    dfn[u] = cnt;
    if (son[u]) dfs2(son[u], tp);
    for (int v : G[u]) {
        if (v == fa[u] || v == son[u]) continue;
        dfs2(v, v);
    }
}

int lca(int u, int v) {
    while (top[u] != top[v]) {
        if (dep[top[u]] < dep[top[v]]) swap(u, v);
        u = fa[top[u]];
    }
    return dep[u] < dep[v] ? u : v;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin >> n;
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        G[u].push_back(v);
        G[v].push_back(u);
    }
    dfs1(1, 0);
    dfs2(1, 1);
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            f[i][j] = g[i][j] = 0;
        }
    }
    for (int i = 1; i <= n; i++) {
        f[i][i] = 1;
        g[i][i] = 1;
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                if (i == j || j == k || i == k) continue;
                int x = lca(i, j), y = lca(j, k), z = lca(i, k);
                if (x == z) {
                    f[i][k] = (f[i][k] + f[i][j] * f[j][k]) % mod;
                    g[i][k] = (g[i][k] + g[i][j] * g[j][k]) % mod;
                } else if (y == z) {
                    f[i][k] = (f[i][k] + f[i][j] * g[j][k]) % mod;
                    g[i][k] = (g[i][k] + g[i][j] * f[j][k]) % mod;
                } else {
                    f[i][k] = (f[i][k] + f[i][j] * f[j][k]) % mod;
                    g[i][k] = (g[i][k] + g[i][j] * g[j][k]) % mod;
                }
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (i == j) continue;
            d[i] = max(d[i], dfn[j] - dfn[i] + 1);
        }
    }
    for (int i = 1; i <= n; i++) {
        ans = (ans + f[i][i] * g[i][i]) % mod;
        for (int j = 1; j <= n; j++) {
            if (i == j) continue;
            ans = (ans + f[i][j] * g[j][i]) % mod;
        }
    }
    for (int i = 1; i <= n; i++) {
        ans = (ans + mod - f[i][i] * g[i][i]) % mod;
        for (int j = 1; j <= n; j++) {
            if (i == j) continue;
            ans = (ans + mod - f[i][j] * g[j][i]) % mod;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (i == j) continue;
            ans = (ans + mod - f[i][j] * g[j][i]) % mod;
        }
    }
    cout << ans << '\n';
    return 0;
}
