import java.io.*;
import java.util.*;

public class Main {
    static final int N = (int) 2e5 + 5;
    static final int mod = 998244353;
    static int n, ans;
    static int[] dep, fa, siz;
    static List<Integer>[] G;

    static int find(int x) {
        return x == fa[x] ? x : (fa[x] = find(fa[x]));
    }

    static void dfs(int u, int f) {
        dep[u] = dep[fa[u] = f] + 1;
        siz[u] = 1;
        for (int v : G[u]) {
            if ((v = find(v)) != f) {
                dfs(v, u);
                siz[u] += siz[v];
            }
        }
    }

    static void solve(int u, int f) {
        int mx = 0;
        for (int v : G[u]) {
            if ((v = find(v)) != f) {
                mx = Math.max(mx, siz[v]);
            }
        }
        if (mx * 2 > siz[u] - 1) {
            ans = (int) ((long) ans * (siz[u] - 1 - mx * 2) % mod * (mx * 2 - 1) % mod * pow(siz[u] - 1 - mx * 2, mod - 2) % mod * pow(mx * 2 - 1, mod - 2) % mod);
        }
        for (int v : G[u]) {
            if ((v = find(v)) != f) {
                ans = (int) ((long) ans * pow(siz[v], mod - 2) % mod);
                solve(v, u);
                ans = (int) ((long) ans * siz[v] % mod);
            }
        }
    }

    static int pow(int x, int y) {
        int res = 1;
        while (y > 0) {
            if ((y & 1) > 0) {
                res = (int) ((long) res * x % mod);
            }
            x = (int) ((long) x * x % mod);
            y >>= 1;
        }
        return res;
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        n = sc.nextInt();
        G = new List[n + 1];
        for (int i = 1; i <= n; i++) {
            G[i] = new ArrayList<>();
        }
        for (int i = 1; i < n; i++) {
            int u = sc.nextInt(), v = sc.nextInt();
            G[u].add(v);
            G[v].add(u);
        }
        dep = new int[n + 1];
        fa = new int[n + 1];
        siz = new int[n + 1];
        dfs(1, 0);
        ans = 1;
        solve(1, 0);
        pw.println(ans);
        pw.close();
    }

    static class Scanner {
        BufferedReader br;
        StringTokenizer st;

        Scanner(InputStream in) {
            br = new BufferedReader(new InputStreamReader(in));
        }

        String next() {
            while (st == null || !st.hasMoreTokens()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }
    }
}
