import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int H = scanner.nextInt();
            int W = scanner.nextInt();
            if (H == 0 && W == 0) break;
            char[][] room = new char[H][];
            for (int i = 0; i < H; i++) {
                room[i] = scanner.next().toCharArray();
            }
            int x = 0, y = 0;
            while (true) {
                if (room[y][x] == '.') {
                    System.out.println(x + " " + y);
                    break;
                }
                if (room[y][x] == '^') {
                    if (y == 0) {
                        System.out.println("LOOP");
                        break;
                    }
                    room[y][x] = '.';
                    y--;
                } else if (room[y][x] == 'v') {
                    if (y == H - 1) {
                        System.out.println("LOOP");
                        break;
                    }
                    room[y][x] = '.';
                    y++;
                } else if (room[y][x] == '<') {
                    if (x == 0) {
                        System.out.println("LOOP");
                        break;
                    }
                    room[y][x] = '.';
                    x--;
                } else if (room[y][x] == '>') {
                    if (x == W - 1) {
                        System.out.println("LOOP");
                        break;
                    }
                    room[y][x] = '.';
                    x++;
                }
            }
        }
    }
}
