import sys
input = sys.stdin.readline

N = 2 * 10 ** 5 + 5
mod = 998244353

def qpow(a, b):
    res = 1
    while b:
        if b & 1:
            res = res * a % mod
        a = a * a % mod
        b >>= 1
    return res

def inv(a):
    return qpow(a, mod - 2)

n = int(input())
fa = [i for i in range(n + 1)]
sz = [1] * (n + 1)
dep = [0] * (n + 1)
d = [[] for _ in range(n + 1)]
e = [[] for _ in range(n + 1)]
ans = 1

def find(x):
    if fa[x] != x:
        fa[x] = find(fa[x])
    return fa[x]

def add(x, y):
    x = find(x)
    y = find(y)
    if x == y:
        return
    if sz[x] < sz[y]:
        x, y = y, x
    fa[y] = x
    sz[x] += sz[y]
    d[x].extend(d[y])
    e[x].extend(e[y])
    d[y].clear()
    e[y].clear()
    sz[y] = 0

for _ in range(n - 1):
    x, y = map(int, input().split())
    add(x, y)

for i in range(1, n + 1):
    dep[i] = dep[find(i)] + 1

for i in range(1, n + 1):
    d[i].append(dep[i])
    e[i].append(dep[i])

for i in range(1, n + 1):
    d[i].sort()
    e[i].sort()

for i in range(1, n + 1):
    if len(d[i]) == 1:
        continue
    for j in range(len(d[i])):
        if d[i][j] == e[i][j]:
            ans = ans * (len(d[i]) - 1) % mod
        else:
            ans = 0
            break

print(ans)
