#include <stdio.h>

int main() {
    char s[3];

    scanf("%s", s);

    int countA = 0, countB = 0;

    for (int i = 0; i < 3; i++) {
        if (s[i] == 'A') {
            countA++;
        } else {
            countB++;
        }
    }

    if (countA > 0 && countB > 0) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }

    return 0;
}
