#include <iostream>
#include <string>

int main() {
    std::string S;
    std::cin >> S;

    int countA = 0, countB = 0;

    for (char c : S) {
        if (c == 'A') {
            countA++;
        } else {
            countB++;
        }
    }

    if (countA > 0 && countB > 0) {
        std::cout << "Yes" << std::endl;
    } else {
        std::cout << "No" << std::endl;
    }

    return 0;
}
