#include <iostream>
#include <cmath>

int main() {
    long long n, a, b;
    std::cin >> n >> a >> b;

    long long sum = 0;
    long long k = 1;
    while (k <= n) {
        long long blue = (a * k) / (a + b);
        sum += blue;
        k = k + blue;
    }

    std::cout << sum << std::endl;

    return 0;
}
