def magic_tile():
    while True:
        try:
            H, W = map(int, input().split())
            room = []
            for _ in range(H):
                room.append(input())
            x, y = 0, 0
            while True:
                if room[y][x] == '>':
                    x = (x + 1) % W
                elif room[y][x] == '<':
                    x = (x - 1) % W
                elif room[y][x] == '^':
                    y = (y - 1) % H
                elif room[y][x] == 'v':
                    y = (y + 1) % H
                elif room[y][x] == '.':
                    break
                if (x, y) == (0, 0):
                    print("LOOP")
                    break
                print(x, y)
        except ValueError:
            break

