import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int A = scanner.nextInt();
        int B = scanner.nextInt();
        int low = 1;
        int high = 1000000;
        while (low <= high) {
            int mid = (low + high) / 2;
            int taxA = (int) Math.floor(mid * 0.08);
            int taxB = (int) Math.floor(mid * 0.1);
            if (taxA == A && taxB == B) {
                System.out.println(mid);
                break;
            } else if (taxA < A || taxB < B) {
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }
        if (low > high) {
            System.out.println(-1);
        }
    }
}
